import React from 'react'

const App = () => {
	const course = 'Half Stack application development'
	const part1 = 'Fundamentals of React'
	const exercises1 = 10
	const part2 = 'Using props to pass data'
	const exercises2 = 7
	const part3 = 'State of a component'
	const exercises3 = 14

	return (
		<div>
			<Header course={course} part1={part1} exercises1={exercises1}/>
			<Content part2={part2} exercises2={exercises2} part3={part3} exercises3/>
			<Total />
		</div>
	)
}

const Header = (course, part1, exercises1) => {
	return(
		<div>
			<h1>{course}</h1>
			<p>
				{part1} {exercises1}
			</p>
		</div>
	)
}

const Content = (part2, exercises2, part3, exercises3) => {
	return (
		<div>
			<p>
				{part2} {exercises2}
			</p>
			<p>
				{part3} {exercises3}
			</p>
		</div>
	)
}

const Total = (exercises1, exercises2, exercises3) => {
	return(
		<p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
	)
}

export default App
